package com.leet_code;public class Remove_Linked_List_Elements{public class ListNode{int val;ListNode next;ListNode(){}ListNode(int val){this.val=val;}ListNode(int val,ListNode next){this.val=val;this.next=next;}}public ListNode removeElements(ListNode head,int val){while(head!=null&&head.val==val){head=head.next;}if(head==null){return null;}ListNode x=head;while(x.next!=null){if(x.next.val==val){x.next=x.next.next;}else{x=x.next;}}return head;}}
